import { describe, expect, test } from 'vitest';
import { mount } from '@vue/test-utils';
import Navbar from '@/components/Navbar.vue';

describe('Navbar.vue', () => {
  test('should toggle the class on click', async () => {
    const wrapper = mount(Navbar);
    const navbarCollapsed = wrapper.get('#navbar').element!;
    // The element with the id `#navbar` should have the class `collapse`
    expect(navbarCollapsed.className).toContain('navbar-collapse collapse');

    // We should have a `button` element to collapse the menu
    const button = wrapper.get('button');
    await button.trigger('click');

    const navbar = wrapper.get('#navbar').element!;
    // The element with the id `#navbar` should have not the class `collapse` after a click
    expect(navbar.className).toContain('navbar-collapse');
    expect(navbar.className).not.toContain('navbar-collapse collapse');
  });
});

import { describe, expect, test } from 'vitest';
import { mount } from '@vue/test-utils';
import { PonyModel } from '@/models/PonyModel';
import Pony from '@/components/Pony.vue';

function ponyWrapper(ponyModel: PonyModel) {
  return mount(Pony, {
    props: {
      ponyModel
    }
  });
}

describe('Pony.vue', () => {
  test('should display an image and a legend', () => {
    const ponyModel: PonyModel = {
      id: 1,
      name: 'Fast Rainbow',
      color: 'PURPLE'
    };

    const wrapper = ponyWrapper(ponyModel);

    // You should have an image for the pony
    const image = wrapper.get('img');
    // The `src` attribute of the image is not correct
    expect(image.attributes('src')).toBe('/images/pony-purple.gif');
    // The `alt` attribute for the image is not correct
    expect(image.attributes('alt')).toBe('Fast Rainbow');
    // You should have a `figcaption` element for the pony
    const legend = wrapper.get('figcaption');
    // The `figcaption` element should display the pony's name
    expect(legend.text()).toContain('Fast Rainbow');
  });

  test('should emit an event on click', () => {
    const ponyModel: PonyModel = {
      id: 1,
      name: 'Fast Rainbow',
      color: 'PURPLE'
    };

    const wrapper = ponyWrapper(ponyModel);

    // You should have a `figure` element for the pony
    const figure = wrapper.get('figure');
    figure.trigger('click');
    // You may have forgotten the click handler on the `figure` element
    // or to emit the `ponySelected` event in the click handler
    expect(wrapper.emitted().ponySelected).toBeTruthy();
  });
});

import { describe, expect, test } from 'vitest';
import { mount } from '@vue/test-utils';
import Race from '@/components/Race.vue';
import Pony from '@/components/Pony.vue';
import { RaceModel } from '@/models/RaceModel';

describe('Race.vue', () => {
  test('should display a race name and its ponies', () => {
    const raceModel = {
      id: 12,
      name: 'Paris',
      ponies: [
        { id: 1, name: 'Gentle Pie', color: 'YELLOW' },
        { id: 2, name: 'Big Soda', color: 'ORANGE' },
        { id: 3, name: 'Gentle Bottle', color: 'PURPLE' },
        { id: 4, name: 'Superb Whiskey', color: 'GREEN' },
        { id: 5, name: 'Fast Rainbow', color: 'BLUE' }
      ],
      startInstant: '2020-02-18T08:02:00Z'
    } as RaceModel;

    const wrapper = mount(Race, {
      props: {
        raceModel
      }
    });

    // You need an h2 element for the race name
    const raceName = wrapper.get('h2');
    // The h2 element should contain the race name
    expect(raceName.text()).toContain('Paris');
    // You need a p element for the race start instant
    const raceStart = wrapper.get('p');
    // The p element should contain the race start instant transformed by the fromNow function
    expect(raceStart.text()).toContain('ago');
    const ponies = wrapper.findAllComponents(Pony);
    // You should have one Pony component per pony
    expect(ponies).toHaveLength(5);
  });
});

